mapper.insert:
INSERT INTO admin (
            admin_id, admin_pw
)
VALUES (
  #{adminId}, #{adminPw}
)

INSERT INTO users (
                    user_no, auth_no, user_id, user_pw, name, address, phone, email, status        
)
VALUES(
  #{userNo}, #{authNo}, #{userId}, #{userPw}, #{name}, #{address}, #{phone}, #{email}, #{status}
)

mapper.update:
UPDATE users
SET auth_no = #{authNo}
  , user_id = #{userId} 
  , user_pw = #{userPw}
  , name = #{name}
  , address = #{address}
  , phone = #{phone}
  , email = #{email}
  , status = #{status}
WHERE user_no = #{userNo}
