mapper.select:
SELECT *
  FROM flight f left join product p on f.route_no = p.route_no 
                left join route r on f.route_no = r.route_no
  WHERE p.departure = #{departure}
    AND p.destination = #{destination}
    AND f.seat_remaining > #{pasCount}
    AND r.departure_date= SUBSTRING_INDEX(#{departureDate}, ' ~ ', 1)

SELECT *
  FROM flight f left join product p on f.route_no = p.route_no
                left join route r on f.route_no = r.route_no
  WHERE p.departure = #{departure}
    AND p.destination = #{destination}
    AND f.seat_remaining > #{pasCount}
    AND r.departure_date= SUBSTRING_INDEX(#{departureDate}, ' ~ ', -1)

mapper.insert:
INSERT INTO passengers ( product_no_dep, product_no_des, passenger_name, first_name, last_name, gender, pin_type, birth, phone, email ) VALUES ( #{productNoDep}, #{productNoDes}, #{passengerName}, #{firstName}, #{lastName}, #{gender}, #{pinType}, #{birth}, #{phone}, #{email} )
SELECT * FROM ticket LEFT JOIN booking ON ticket.booking_no = #{bookingNo}
SELECT * FROM seat ORDER BY CAST(SUBSTRING(seat_no, 2) AS SIGNED), seat_no;
